import React, { useState, useEffect } from 'react';
import { 
  Box, 
  IconButton, 
  Typography, 
  Tooltip, 
  CircularProgress,
  Button,
  Stack,
  useTheme,
  alpha
} from '@mui/material';
import { 
  Mic as MicIcon, 
  Stop as StopIcon, 
  Send as SendIcon,
  Delete as DeleteIcon,
  VolumeUp as VolumeUpIcon
} from '@mui/icons-material';
import useAudioRecorder from '../../hooks/useAudioRecorder';
import AudioVisualizer from './AudioVisualizer';

interface AudioRecorderProps {
  /**
   * Callback when recording is complete and user confirms to send
   */
  onSend: (audioBlob: Blob) => void;

  /**
   * Callback when recording is cancelled
   */
  onCancel?: () => void;

  /**
   * Maximum recording duration in milliseconds
   */
  maxDuration?: number;

  /**
   * Whether to show the preview of the recorded audio
   */
  showPreview?: boolean;

  /**
   * Custom class name for styling
   */
  className?: string;
}

/**
 * AudioRecorder component that provides recording functionality with visualization
 */
const AudioRecorder: React.FC<AudioRecorderProps> = ({
  onSend,
  onCancel,
  maxDuration = 60000, // 1 minute default
  showPreview = true,
  className = '',
}) => {
  const theme = useTheme();
  const [isHovered, setIsHovered] = useState(false);
  const [isRecording, setIsRecording] = useState(false);

  const {
    startRecording,
    stopRecording,
    reset,
    isRecording: isCurrentlyRecording,
    duration,
    formattedDuration,
    audioBlob,
    audioUrl,
    error,
    getAnalyserData
  } = useAudioRecorder({
    maxDuration,
    onRecordingComplete: () => {
      // Auto-stop when max duration is reached
      setIsRecording(false);
    },
  });

  // Update local state when recording state changes
  useEffect(() => {
    if (isCurrentlyRecording !== isRecording) {
      setIsRecording(isCurrentlyRecording);
    }
  }, [isCurrentlyRecording]);

  // Clean up on unmount
  useEffect(() => {
    return () => {
      if (audioUrl) {
        URL.revokeObjectURL(audioUrl);
      }
    };
  }, [audioUrl]);

  const handleStartRecording = () => {
    startRecording();
    setIsRecording(true);
  };

  const handleStopRecording = () => {
    stopRecording();
    setIsRecording(false);
  };

  const handleCancel = () => {
    reset();
    if (onCancel) onCancel();
  };

  const handleSend = () => {
    if (audioBlob) {
      onSend(audioBlob);
      reset();
    }
  };

  const handlePlayPreview = () => {
    if (!audioUrl) return;

    const audio = new Audio(audioUrl);
    audio.play().catch(error => {
      console.error('Error playing audio:', error);
    });
  };

  // Get analyzer data for visualization
  const analyserData = getAnalyserData();

  return (
    <Box 
      className={`audio-recorder ${className}`}
      sx={{
        width: '100%',
        borderRadius: 2,
        overflow: 'hidden',
        transition: 'all 0.3s ease',
        bgcolor: isRecording 
          ? alpha(theme.palette.error.main, 0.1) 
          : theme.palette.background.paper,
        border: `1px solid ${
          isRecording 
            ? theme.palette.error.main 
            : theme.palette.divider
        }`,
        '&:hover': {
          boxShadow: theme.shadows[2],
        },
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <Box sx={{ p: 2 }}>
        {/* Recording state */}
        {isRecording ? (
          <Box sx={{ textAlign: 'center' }}>
            <AudioVisualizer 
              analyser={analyserData?.analyser || null}
              width="100%"
              height={60}
              color={theme.palette.error.main}
              pulse={isRecording}
            />

            <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mt: 1 }}>
              <Box 
                sx={{
                  width: 12,
                  height: 12,
                  borderRadius: '50%',
                  bgcolor: 'error.main',
                  mr: 1,
                  animation: 'pulse 1.5s infinite',
                  '@keyframes pulse': {
                    '0%': { opacity: 1 },
                    '50%': { opacity: 0.3 },
                    '100%': { opacity: 1 },
                  },
                }} 
              />
              <Typography variant="caption" color="error">
                Recording: {formattedDuration}
              </Typography>
            </Box>

            <Box sx={{ mt: 2, display: 'flex', justifyContent: 'center', gap: 2 }}>
              <Button
                variant="contained"
                color="error"
                onClick={handleStopRecording}
                startIcon={<StopIcon />}
                size="small"
              >
                Stop
              </Button>
            </Box>
          </Box>
        ) : audioBlob && showPreview ? (
          // Preview mode after recording
          <Box>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              <IconButton 
                onClick={handlePlayPreview}
                color="primary"
                size="small"
                sx={{ mr: 1 }}
              >
                <VolumeUpIcon />
              </IconButton>

              <Box sx={{ flexGrow: 1 }}>
                <Typography variant="body2">
                  Audio recorded: {formattedDuration}
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  Click play to preview
                </Typography>
              </Box>

              <Stack direction="row" spacing={1}>
                <Tooltip title="Delete recording">
                  <IconButton 
                    onClick={handleCancel}
                    size="small"
                    color="error"
                  >
                    <DeleteIcon fontSize="small" />
                  </IconButton>
                </Tooltip>

                <Tooltip title="Send recording">
                  <IconButton 
                    onClick={handleSend}
                    color="primary"
                    size="small"
                  >
                    <SendIcon fontSize="small" />
                  </IconButton>
                </Tooltip>
              </Stack>
            </Box>
          </Box>
        ) : (
          // Initial state - show record button
          <Box 
            sx={{ 
              display: 'flex', 
              flexDirection: 'column', 
              alignItems: 'center',
              py: 2,
              cursor: 'pointer',
              '&:hover': {
                '& .record-button': {
                  transform: 'scale(1.1)',
                },
              },
            }}
            onClick={handleStartRecording}
          >
            <Box
              className="record-button"
              sx={{
                width: 60,
                height: 60,
                borderRadius: '50%',
                bgcolor: 'error.main',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                mb: 1,
                transition: 'transform 0.2s ease',
                boxShadow: theme.shadows[4],
                '&:hover': {
                  bgcolor: theme.palette.error.dark,
                },
              }}
            >
              <MicIcon sx={{ color: 'common.white', fontSize: 30 }} />
            </Box>

            <Typography variant="body2" color="text.secondary">
              Tap to record a voice message
            </Typography>
            <Typography variant="caption" color="text.disabled">
              Hold to record, release to send
            </Typography>
          </Box>
        )}

        {error && (
          <Typography 
            variant="caption" 
            color="error" 
            sx={{ display: 'block', mt: 1, textAlign: 'center' }}
          >
            {error}
          </Typography>
        )}
      </Box>

      {/* Progress bar for recording duration */}
      {isRecording && (
        <Box 
          sx={{
            height: 4,
            bgcolor: 'divider',
            position: 'relative',
            overflow: 'hidden',
          }}
        >
          <Box 
            sx={{
              position: 'absolute',
              top: 0,
              left: 0,
              height: '100%',
              bgcolor: 'error.main',
              width: `${(duration / (maxDuration / 1000)) * 100}%`,
              transition: 'width 0.1s linear',
            }}
          />
        </Box>
      )}
    </Box>
  );
};

export default AudioRecorder;
