import React, { useState, useEffect, useRef } from 'react';
import { Box, Typography, IconButton, TextField, Paper, Avatar, Badge, useTheme } from '@mui/material';
import { ArrowBack, Send, Mic, AttachFile } from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import { API_BASE_URL } from '../config';
import ChatMessage from '../components/chat/ChatMessage';
import AudioRecorder from '../components/chat/AudioRecorder';

interface Message {
  _id: string;
  sender: { _id: string; name: string; avatar?: string };
  receiver?: { _id: string; name: string; avatar?: string };
  content: string;
  audioUrl?: string;
  createdAt: string;
  status: 'sending' | 'sent' | 'failed';
}

interface UserFromAuth {
    _id: string;
    name: string;
    email: string;
    role: 'doctor' | 'patient';
    token: string;
    avatar?: string;
}

const ChatPageWithAudio: React.FC = () => {
  const { userId: chatId } = useParams<{ userId: string }>();
  const { user, initialLoading, token } = useAuth();
  const currentUser = user as UserFromAuth | null;
  const theme = useTheme();
  const navigate = useNavigate();
  const [otherUser, setOtherUser] = useState<{_id: string, name: string, role: string} | null>(null);

  const [messages, setMessages] = useState<Message[]>([]);
  const [message, setMessage] = useState('');
  const [showAudioRecorder, setShowAudioRecorder] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const audioRefs = useRef<{[key: string]: HTMLAudioElement}>({});

  // Fetch chat data and other user's info
  useEffect(() => {
    console.log('useEffect triggered with chatId:', chatId);
    if (!chatId || initialLoading) return;

    const fetchChatData = async () => {
      console.log('Starting to fetch chat data...');
      try {
        console.log('Fetching current user info...');
        // First, fetch the current user's info to determine their role
        const currentUserRes = await axios.get(`${API_BASE_URL}/users/me`, {
          headers: {
            'x-auth-token': token || ''
          }
        });

        console.log('Current user response:', currentUserRes.data);
        const currentUserRole = currentUserRes.data.role;
        console.log('Current user role:', currentUserRole);

        // If current user is a patient, fetch doctor's info
        console.log('Current user is a', currentUserRole);
        if (currentUserRole === 'patient') {
          try {
            // Fetch the doctor's info
            console.log('Fetching doctors list...');
            const doctorRes = await axios.get(`${API_BASE_URL}/users/doctors`, {
              headers: {
                'x-auth-token': token || ''
              }
            });

            console.log('Doctors list response:', doctorRes.data);
            // Find the specific doctor from the list
            const doctor = doctorRes.data.find((d: any) => d._id === chatId);
            console.log('Found doctor:', doctor);

            if (doctor) {
              setOtherUser({
                _id: doctor._id,
                name: doctor.name,
                role: 'doctor'
              });
            } else {
              console.error('Doctor not found');
              setOtherUser({
                _id: chatId,
                name: 'Doctor',
                role: 'doctor'
              });
            }
          } catch (err) {
            console.error('Error fetching doctor info:', err);
            setOtherUser({
              _id: chatId,
              name: 'Doctor',
              role: 'doctor'
            });
          }
        } else {
          // If current user is a doctor, fetch patient's info
          // This would be implemented similarly with a patients endpoint
          setOtherUser({
            _id: chatId,
            name: 'Patient',
            role: 'patient'
          });
        }

        // Fetch message history
        if (chatId) {
          console.log('Fetching conversation history...');
          const messagesRes = await axios.get(`${API_BASE_URL}/chat/conversation/${chatId}`, {
            headers: { 'x-auth-token': token || '' }
          });
          console.log('Conversation history response:', messagesRes.data);
          setMessages(messagesRes.data);
        }
      } catch (error) {
        console.error('Error fetching chat data:', error);
        setOtherUser({
          _id: chatId,
          name: 'User',
          role: 'user'
        });
      }
    };

    fetchChatData();
  }, [chatId]);

  // Get the other user's name (in a real app, this would come from the API)
  const getOtherUserName = () => {
    if (otherUser?.name) return otherUser.name;
    return 'Loading...';
  };

  // Get initials for avatar
  const getInitials = (name: string) => {
    if (!name) return 'U';
    return name
      .split(' ')
      .map(part => part[0])
      .join('')
      .slice(0, 2)
      .toUpperCase();
  };

  const handleSendMessage = async (content: string, audioBlob?: Blob) => {
    if ((!content.trim() && !audioBlob) || !currentUser || !chatId) return;

    // TODO: Implement audio file upload and get URL
    if (audioBlob) {
      console.log('Audio sending not implemented yet.');
      return;
    }

    const tempId = `temp-${Date.now()}`;
    const newMessage: Message = {
      _id: tempId,
      sender: { _id: currentUser._id, name: currentUser.name, avatar: currentUser.avatar },
      content,
      createdAt: new Date().toISOString(),
      status: 'sending',
    };

    setMessages(prev => [...prev, newMessage]);
    setMessage('');

    try {
      const res = await axios.post(
        `${API_BASE_URL}/chat/send/${chatId}`,
        { content },
        { headers: { 'x-auth-token': token || '' } }
      );

      setMessages(prev =>
        prev.map(msg => (msg._id === tempId ? { ...res.data, status: 'sent' } : msg))
      );
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages(prev =>
        prev.map(msg => (msg._id === tempId ? { ...msg, status: 'failed' } : msg))
      );
    }
  };

  const handlePlayAudio = (url: string) => {
    // Stop any currently playing audio
    Object.values(audioRefs.current).forEach(audio => {
      if (!audio.paused) {
        audio.pause();
        audio.currentTime = 0;
      }
    });

    // Play the selected audio
    const audio = new Audio(url);
    audioRefs.current[url] = audio;
    audio.play().catch(console.error);
  };

  if (initialLoading) {
    return <Box>Loading...</Box>;
  }

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      {/* Header */}
      <Paper sx={{ p: 2, display: 'flex', alignItems: 'center' }}>
        <IconButton onClick={() => navigate(-1)}><ArrowBack /></IconButton>
        <Avatar sx={{ ml: 2, bgcolor: 'primary.main' }}>
          {getInitials(getOtherUserName())}
        </Avatar>
        <Box sx={{ ml: 2 }}>
          <Typography>{getOtherUserName()}</Typography>
          <Typography variant="caption" color="text.secondary">
            {otherUser?.role === 'doctor' ? 'Doctor' : 'Patient'}
          </Typography>
        </Box>
      </Paper>

      {/* Messages */}
      <Box sx={{ flex: 1, overflow: 'auto', p: 2, bgcolor: theme.palette.grey[50] }}>
        {messages.map(msg => (
          <ChatMessage
            key={msg._id}
            message={msg}
            isOwnMessage={msg.sender._id === currentUser?._id}
            onPlayAudio={msg.audioUrl ? () => handlePlayAudio(msg.audioUrl!) : undefined}
          />
        ))}
        <div ref={messagesEndRef} />
      </Box>

      {/* Audio Recorder */}
      {showAudioRecorder && (
        <Box sx={{ p: 2, borderTop: `1px solid ${theme.palette.divider}` }}>
          <AudioRecorder 
            onSend={(blob) => {
              handleSendMessage('Voice message', blob);
              setShowAudioRecorder(false);
            }}
            onCancel={() => setShowAudioRecorder(false)}
          />
        </Box>
      )}

      {/* Message Input */}
      <Box sx={{ p: 2, display: 'flex', gap: 1, borderTop: `1px solid ${theme.palette.divider}` }}>
        <IconButton><AttachFile /></IconButton>
        <TextField
          fullWidth
          variant="outlined"
          id="chat-message-input"
          label="Type a message"
          aria-label="Type a message"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSendMessage(message)}
          disabled={showAudioRecorder}
          size="small"
          sx={{ 
            bgcolor: 'background.paper', 
            borderRadius: 2,
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                border: 'none',
              },
              '&:hover fieldset': {
                border: 'none',
              },
              '&.Mui-focused fieldset': {
                border: 'none',
              },
            },
          }}
          InputLabelProps={{
            shrink: false,
            'aria-hidden': true,
          }}
        />
        <IconButton 
          onClick={() => showAudioRecorder ? setShowAudioRecorder(false) : setShowAudioRecorder(true)}
          color={showAudioRecorder ? 'primary' : 'default'}
        >
          <Mic />
        </IconButton>
        <IconButton 
          onClick={() => handleSendMessage(message)}
          disabled={!message.trim() || showAudioRecorder}
          color="primary"
        >
          <Send />
        </IconButton>
      </Box>
    </Box>
  );
};

export default ChatPageWithAudio;
