import React, { useState, useEffect, useRef } from 'react';
import { Box, Typography, IconButton, TextField, Paper, Avatar, Badge, useTheme } from '@mui/material';
import { ArrowBack, Send, Mic, AttachFile } from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import ChatMessage from '../components/chat/ChatMessage';
import AudioRecorder from '../components/chat/AudioRecorder';

interface Message {
  _id: string;
  sender: { _id: string; name: string; avatar?: string };
  content: string;
  audioUrl?: string;
  createdAt: string;
  status: 'sending' | 'sent' | 'failed';
}

const ChatPageWithAudio: React.FC = () => {
  const { chatId } = useParams<{ chatId: string }>();
  const { user: currentUser } = useAuth();
  const theme = useTheme();
  const navigate = useNavigate();
  const [otherUser, setOtherUser] = useState<{_id: string, name: string, role: string} | null>(null);

  const [messages, setMessages] = useState<Message[]>([]);
  const [message, setMessage] = useState('');
  const [showAudioRecorder, setShowAudioRecorder] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const audioRefs = useRef<{[key: string]: HTMLAudioElement}>({});

  // Fetch chat data and other user's info
  useEffect(() => {
    if (!chatId) return;

    // In a real app, you would fetch the conversation and other user's data here
    // For now, we'll use the chatId as the other user's ID
    const fetchChatData = async () => {
      try {
        // This would be an API call to get the other user's info
        // const response = await axios.get(`${API_BASE_URL}/users/${chatId}`);
        // setOtherUser(response.data);

        // For now, we'll just set a placeholder
        setOtherUser({
          _id: chatId,
          name: 'Loading...',
          role: 'doctor' // This would come from the API
        });

        // Fetch messages for this chat
        // const messagesRes = await axios.get(`${API_BASE_URL}/chat/messages/${chatId}`);
        // setMessages(messagesRes.data);
      } catch (error) {
        console.error('Error fetching chat data:', error);
      }
    };

    fetchChatData();
  }, [chatId]);

  // Get the other user's name (in a real app, this would come from the API)
  const getOtherUserName = () => {
    if (otherUser?.name) return otherUser.name;
    return 'Loading...';
  };

  // Get initials for avatar
  const getInitials = (name: string) => {
    if (!name) return 'U';
    return name
      .split(' ')
      .map(part => part[0])
      .join('')
      .slice(0, 2)
      .toUpperCase();
  };

  const handleSendMessage = (content: string, audioBlob?: Blob) => {
    if ((!content.trim() && !audioBlob) || !currentUser) return;

    const tempId = `temp-${Date.now()}`;
    const newMessage: Message = {
      _id: tempId,
      sender: { _id: currentUser._id, name: currentUser.name },
      content,
      audioUrl: audioBlob ? URL.createObjectURL(audioBlob) : undefined,
      createdAt: new Date().toISOString(),
      status: 'sending',
    };

    setMessages(prev => [...prev, newMessage]);
    setMessage('');

    // Simulate sending
    setTimeout(() => {
      setMessages(prev => 
        prev.map(msg => 
          msg._id === tempId ? { ...msg, status: 'sent' } : msg
        )
      );
    }, 1000);
  };

  const handlePlayAudio = (url: string) => {
    // Stop any currently playing audio
    Object.values(audioRefs.current).forEach(audio => {
      if (!audio.paused) {
        audio.pause();
        audio.currentTime = 0;
      }
    });

    // Play the selected audio
    const audio = new Audio(url);
    audioRefs.current[url] = audio;
    audio.play().catch(console.error);
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      {/* Header */}
      <Paper sx={{ p: 2, display: 'flex', alignItems: 'center' }}>
        <IconButton onClick={() => navigate(-1)}><ArrowBack /></IconButton>
        <Avatar sx={{ ml: 2, bgcolor: 'primary.main' }}>
          {getInitials(getOtherUserName())}
        </Avatar>
        <Box sx={{ ml: 2 }}>
          <Typography>{getOtherUserName()}</Typography>
          <Typography variant="caption" color="text.secondary">
            {otherUser?.role === 'doctor' ? 'Doctor' : 'Patient'}
          </Typography>
        </Box>
      </Paper>

      {/* Messages */}
      <Box sx={{ flex: 1, overflow: 'auto', p: 2, bgcolor: theme.palette.grey[50] }}>
        {messages.map(msg => (
          <ChatMessage
            key={msg._id}
            message={msg}
            isOwnMessage={msg.sender._id === currentUser?._id}
            onPlayAudio={msg.audioUrl ? () => handlePlayAudio(msg.audioUrl!) : undefined}
          />
        ))}
        <div ref={messagesEndRef} />
      </Box>

      {/* Audio Recorder */}
      {showAudioRecorder && (
        <Box sx={{ p: 2, borderTop: `1px solid ${theme.palette.divider}` }}>
          <AudioRecorder 
            onSend={(blob) => {
              handleSendMessage('Voice message', blob);
              setShowAudioRecorder(false);
            }}
            onCancel={() => setShowAudioRecorder(false)}
          />
        </Box>
      )}

      {/* Message Input */}
      <Box sx={{ p: 2, display: 'flex', gap: 1, borderTop: `1px solid ${theme.palette.divider}` }}>
        <IconButton><AttachFile /></IconButton>
        <TextField
          fullWidth
          variant="outlined"
          id="chat-message-input"
          label="Type a message"
          aria-label="Type a message"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSendMessage(message)}
          disabled={showAudioRecorder}
          size="small"
          sx={{ 
            bgcolor: 'background.paper', 
            borderRadius: 2,
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                border: 'none',
              },
              '&:hover fieldset': {
                border: 'none',
              },
              '&.Mui-focused fieldset': {
                border: 'none',
              },
            },
          }}
          InputLabelProps={{
            shrink: false,
            'aria-hidden': true,
          }}
        />
        <IconButton 
          onClick={() => showAudioRecorder ? setShowAudioRecorder(false) : setShowAudioRecorder(true)}
          color={showAudioRecorder ? 'primary' : 'default'}
        >
          <Mic />
        </IconButton>
        <IconButton 
          onClick={() => handleSendMessage(message)}
          disabled={!message.trim() || showAudioRecorder}
          color="primary"
        >
          <Send />
        </IconButton>
      </Box>
    </Box>
  );
};

export default ChatPageWithAudio;
