import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { AuthProvider } from './context/AuthContext';
import { SocketProvider } from './context/SocketContext';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import PatientDashboard from './pages/PatientDashboard';
import DoctorDashboard from './pages/DoctorDashboard';
import ChatPage from './pages/ChatPage';
import PrivateRoute from './components/common/PrivateRoute';

const theme = createTheme({
  palette: {
    primary: { main: '#4a6bff', light: '#7d95ff', dark: '#0045cc', contrastText: '#fff' },
    secondary: { main: '#6e3bde', light: '#9d6bff', dark: '#4d2a9a', contrastText: '#fff' },
    error: { main: '#ff3b5c' },
    success: { main: '#00c853' },
    warning: { main: '#ffab00' },
    info: { main: '#00b8d4' },
    background: { default: '#f8fafc', paper: '#fff' },
    text: { primary: '#1e293b', secondary: '#64748b', disabled: '#94a3b8' },
    divider: 'rgba(226, 232, 240, 0.6)',
  },
  typography: {
    fontFamily: '"Inter", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
    h1: { fontWeight: 700, fontSize: '2.5rem', lineHeight: 1.2, letterSpacing: '-0.02em' },
    h2: { fontWeight: 700, fontSize: '2rem', lineHeight: 1.3, letterSpacing: '-0.015em' },
    h3: { fontWeight: 600, fontSize: '1.75rem', lineHeight: 1.4 },
    button: { textTransform: 'none', fontWeight: 600 },
  },
  shape: { borderRadius: 8 },
  components: {
    MuiButton: {
      styleOverrides: {
        root: { borderRadius: 8, padding: '8px 16px', textTransform: 'none' },
        contained: { boxShadow: 'none', '&:hover': { boxShadow: '0 2px 8px rgba(0,0,0,0.1)' } }
      }
    },
    MuiCard: {
      styleOverrides: {
        root: { 
          borderRadius: 12, 
          boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
          '&:hover': { boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }
        }
      }
    },
    MuiTextField: {
      styleOverrides: {
        root: { '& .MuiOutlinedInput-root': { borderRadius: 8 } }
      }
    },
  }
});

const App: React.FC = () => {

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <AuthProvider>
          <SocketProvider>
            <Routes>
              <Route path="/login" element={<LoginPage />} />
              <Route path="/register" element={<RegisterPage />} />
              <Route path="/" element={<Navigate to="/login" />} />
              <Route 
                path="/patient-dashboard" 
                element={
                  <PrivateRoute allowedRoles={['patient']}>
                    <PatientDashboard />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/doctor-dashboard" 
                element={
                  <PrivateRoute allowedRoles={['doctor']}>
                    <DoctorDashboard />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/chat/:userId" 
                element={
                  <PrivateRoute allowedRoles={['doctor', 'patient']}>
                    <ChatPage />
                  </PrivateRoute>
                } 
              />
            </Routes>
          </SocketProvider>
        </AuthProvider>
      </Router>
    </ThemeProvider>
  );
};

export default App;
